var http = require('http');

http.get({
  host: 'koloritfest.org',
  path: '/internal/all-participants',
  headers: {
    'User-Agent': 'blah'
  }
}, function(res) {
  if (res.statusCode === 200) {
    var body = [];
    res.setEncoding('utf8');
    res.on('data', function(chunk) {
      body.push(chunk);
    });
    res.on('end', function() {
      body = body.join('');
      console.log(body);
    });
  } else {
    console.log('Received non-ok response: ' + res.statusCode);
  }
}).on('error', function(e) {
  console.log("Got error: " + e.message);
});